mapper.select:
select bno, title, content, writer, regdate, viewcnt
from tbl_board
where bno = #{bno}

mapper.update:
update tbl_board
set title = #{title}, content = #{content}
where bno = #{bno}

update tbl_board
set replycnt = replycnt + #{amount}
where bno = #{bno}

update tbl_board
set viewcnt = viewcnt + 1
where bno = #{bno}
